import { appModel } from "../Models/AppModel";
import "../Styles/CodeBox.css";
import { useContext, useState, useRef } from "react";

const CodeField = () => {
  const { setInput, userInput } = useContext(appModel);
  const [lineNumber, setLines] = useState([""]);
  function countLines(text, textarea) {
    const lineCount = Math.floor(textareaRef.current.scrollHeight / 2);
    setLines(new Array(lineCount).fill(""));
    setLines(text.split("\n"));
  }
  const textareaRef = useRef(null);
  return (
    <>
      <div className="InputContainer">
        <div className="InputBoxLeft">
          {lineNumber.map((text, index) => {
            return <p className="InputLineNum">{index}</p>;
          })}
        </div>
        <div className="InputBox">
          <textarea
            className="codeInput"
            value={userInput}
            onChange={(e) => {
              setInput(e.target.value);
              countLines(e.target.value);
            }}
          />
        </div>
      </div>
    </>
  );
};
export default CodeField;
